mapper.update:
	update MATRIX_BILLING_ORDER_SUB


            DISCOUNT_PRICE=#{discountPrice},

        UPDATE_TIME=now(),

            UPDATE_USER=#{updateUser}


	where ID=#{orderId}

mapper.sql:
where 1=1


        and orderSub.ID=#{id}


        and orderSub.ORDER_ID=#{orderId}


        and orderSub.SUBSCRIPTION_ID=#{subscriptionId}


        and orderSub.START_TIME=#{startTime}


        and orderSub.END_TIME=#{endTime}


        and orderSub.PRICE=#{price}


        and orderSub.DISCOUNT_PRICE=#{discountPrice}


        and orderSub.DELETED=#{deleted}


        and orderSub.CREATE_TIME=#{createTime}


        and orderSub.UPDATE_TIME=#{updateTime}


        and orderSub.CREATE_USER=#{createUser}


        and orderSub.UPDATE_USER=#{updateUser}


        and orderSub.DESCN=#{descn}

mapper.select:
select ,sub.PRODUCT_ID,sub.BASE_REGION_ID,sub.BUY_TYPE, sub.CHARGE_TYPE,sub.ORDER_TIME,sub.VALID,sub.PRODUCT_INFO_RECORD_ID,product.NAME PRODUCT_NAME,product.DESCN PRODUCT_DESCN,region.NAME BASE_REGION_NAME, od.ORDER_NUMBER,od.STATUS,od.PAY_NUMBER,od.PAY_TIME,od.DESCN ORDER_DESCN,od.CREATE_TIME ORDER_CREATE_TIME, record.PARAMS,record.PRODUCT_ID RECORD_PRODUCT_ID,record.INVOKE_TYPE, record.ID RECORD_ID,record.INSTANCE_ID,record.DESCN RECORD_DESCN,record.BATCH,od.ACCOUNT_PRICE from MATRIX_BILLING_ORDER_SUB orderSub left join MATRIX_BILLING_ORDER od on orderSub.ORDER_ID=od.ID left join MATRIX_BILLING_SUBSCRIPTION sub on orderSub.SUBSCRIPTION_ID=sub.ID left join MATRIX_BILLING_PRODUCT product on sub.PRODUCT_ID=product.ID left join MATRIX_BILLING_BASE_REGION region on sub.BASE_REGION_ID=region.ID left join MATRIX_PRODUCT_INFO_RECORD record on sub.PRODUCT_INFO_RECORD_ID=record.ID
and od.ORDER_NUMBER = #{orderNumber}
and sub.USER_ID = #{userId}

select ,sub.PRODUCT_ID,sub.BASE_REGION_ID, sub.CHARGE_TYPE,sub.ORDER_TIME,sub.VALID,sub.PRODUCT_INFO_RECORD_ID, product.NAME PRODUCT_NAME,region.NAME BASE_REGION_NAME from MATRIX_BILLING_ORDER_SUB orderSub left join MATRIX_BILLING_SUBSCRIPTION sub on orderSub.SUBSCRIPTION_ID=sub.ID left join MATRIX_BILLING_PRODUCT product on sub.PRODUCT_ID=product.ID left join MATRIX_BILLING_BASE_REGION region on sub.BASE_REGION_ID=region.ID
ORDER BY od.CREATE_TIME DESC
limit #{page.startRowPosition},#{page.recordsPerPage}
