mapper.sql:
where 1=1


        and subscription.ID=#{id}


        and subscription.SUBSCRIPTION_NUMBER like CONCAT('%','${subscriptionNumber}','%') escape '^'


        and subscription.PRODUCT_ID=#{productId}


        and subscription.BASE_REGION_ID=#{baseRegionId}


        and subscription.BUY_TYPE=#{buyType}


        and subscription.CHARGE_TYPE=#{chargeType}


        and subscription.ORDER_TIME=#{orderTime}


        and subscription.VALID=#{valid}


        and subscription.START_TIME=#{startTime}


        and subscription.END_TIME=#{endTime}


        and subscription.USER_ID=#{userId}


        and subscription.DELETED=#{deleted}


        and subscription.CREATE_TIME=#{createTime}


        and subscription.UPDATE_TIME=#{updateTime}


        and subscription.CREATE_USER=#{createUser}


        and subscription.UPDATE_USER=#{updateUser}


        and subscription.DESCN=#{descn}


        and subscription.PRODUCT_INFO_RECORD_ID=#{productInfoRecordId}
